import React from "react";
import Slider from "react-slick";
import "./topCategories.css";
import CategoryCard from "./categoryCard";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

function TopCategories() {
  const settings = {
    dots: false, // Remove dots navigation
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    arrows: true, // Show arrows
    prevArrow: <CustomPrevArrow />, // Custom previous arrow component
    nextArrow: <CustomNextArrow />, // Custom next arrow component
  };

  // Custom previous arrow component
  function CustomPrevArrow(props) {
    const { className, onClick } = props;
    return (
      <div className={`${className} custom-prev-arrow`} onClick={onClick}>
        &lt;
      </div>
    );
  }

  // Custom next arrow component
  function CustomNextArrow(props) {
    const { className, onClick } = props;
    return (
      <div className={`${className} custom-next-arrow`} onClick={onClick}>
        &gt;
      </div>
    );
  }

  return (
    <div className="topCategories">
      <h2 className="categoryHeading">Top Categories</h2>
      <div className="categories-carousel">
        <Slider {...settings}>
          {/* Your CategoryCard components */}
        </Slider>
      </div>
    </div>
  );
}

export default TopCategories;
