import React, { useState } from "react";
import "../../components/header/headerPrimary.css";
import ShoppingCartOutlinedIcon from "@material-ui/icons/ShoppingCartOutlined";
import SearchOutlinedIcon from "@material-ui/icons/SearchOutlined";

function HeaderPrimary() {
  // State to track whether the signup popup is open or closed
  const [isSignupOpen, setSignupOpen] = useState(false);

  // Function to open the signup popup
  const openSignupPopup = () => {
    setSignupOpen(true);
  };

  // Function to close the signup popup
  const closeSignupPopup = () => {
    setSignupOpen(false);
  };

  return (
    <div className="headerPrimary">
      {/* ... your existing code ... */}
      <div className="right part">
        {/* ... your existing code ... */}
        <div className="signup button" onClick={openSignupPopup}>
          Sign up
        </div>
      </div>

      {/* Signup Popup */}
      {isSignupOpen && (
        <div className="signup-popup">
          <div className="popup-content">
            <h2>Sign Up</h2>
            <label htmlFor="username">Username:</label>
            <input type="text" id="username" />
            <label htmlFor="email">Email:</label>
            <input type="email" id="email" />
            <label htmlFor="password">Password:</label>
            <input type="password" id="password" />
            <label htmlFor="role">Role:</label>
            <input type="text" id="role" />
            <button onClick={closeSignupPopup}>Close</button>
            {/* Add form submission logic here */}
          </div>
        </div>
      )}
    </div>
  );
}

export default HeaderPrimary;
