import React, { useState } from "react";
import "../../components/header/headerPrimary.css";
import ShoppingCartOutlinedIcon from "@material-ui/icons/ShoppingCartOutlined";
import SearchOutlinedIcon from "@material-ui/icons/SearchOutlined";

function HeaderPrimary() {
  const [isSignupOpen, setSignupOpen] = useState(false); // To control the visibility of the signup popup
  const [userData, setUserData] = useState({
    username: "",
    email: "",
    password: "",
    role: "",
  });

  // Function to toggle the signup popup
  const toggleSignupPopup = () => {
    setSignupOpen(!isSignupOpen);
  };

  // Function to handle form input changes
  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setUserData({
      ...userData,
      [name]: value,
    });
  };

  // Function to handle signup form submission
  const handleSignup = () => {
    // You can perform signup logic here or send the data to your backend
    console.log("User Data:", userData);

    // Close the signup popup
    setSignupOpen(false);
  };

  return (
    <div className="headerPrimary">
      {/* Your existing header content */}
      {/* ... */}

      <div className="right part">
        {/* Add an onClick event to open the signup popup */}
        <div className="signup button" onClick={toggleSignupPopup}>
          Sign up
        </div>
      </div>

      {/* Signup Popup */}
      {isSignupOpen && (
        <div className="signup-popup">
          <div className="popup-content">
            <h2>Sign Up</h2>
            <input
              type="text"
              name="username"
              placeholder="Username"
              value={userData.username}
              onChange={handleInputChange}
            />
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={userData.email}
              onChange={handleInputChange}
            />
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={userData.password}
              onChange={handleInputChange}
            />
            <input
              type="text"
              name="role"
              placeholder="Role"
              value={userData.role}
              onChange={handleInputChange}
            />
            <button onClick={handleSignup}>Sign Up</button>
            <div className="close-popup" onClick={toggleSignupPopup}>
              Close
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default HeaderPrimary;
